local base64MoneyIcon = 'data:image/png;base64,'

RegisterNetEvent('esx:setJob')
AddEventHandler('esx:setJob', function(job)
	ESX.PlayerData.job = job
	RefreshBussHUD()
end)

function RefreshBussHUD()
	DisableSocietyMoneyHUDElement()

	if ESX.PlayerData.job.grade_name == Config.BossGrade then
		EnableSocietyMoneyHUDElement()

		ESX.TriggerServerCallback('esx_society:getSocietyMoney', function(money)
			UpdateSocietyMoneyHUDElement(money)
		end, ESX.PlayerData.job.name)
	end
end

RegisterNetEvent('esx_addonaccount:setMoney')
AddEventHandler('esx_addonaccount:setMoney', function(society, money)
	if ESX.PlayerData.job and ESX.PlayerData.job.grade_name == Config.BossGrade and 'society_' .. ESX.PlayerData.job.name == society then
		UpdateSocietyMoneyHUDElement(money)
	end
end)

function EnableSocietyMoneyHUDElement()
	local societyMoneyHUDElementTpl = '<div><img src="' .. base64MoneyIcon .. '" style="width:20px; height:20px; vertical-align:middle;">&nbsp;{{money}}</div>'

	if ESX.GetConfig().EnableHud then
		ESX.UI.HUD.RegisterElement('society_money', 3, 0, societyMoneyHUDElementTpl, {
			money = 0
		})
	end

	TriggerEvent('esx_society:toggleSocietyHud', true)
end

function DisableSocietyMoneyHUDElement()
	if ESX.GetConfig().EnableHud then
		ESX.UI.HUD.RemoveElement('society_money')
	end

	TriggerEvent('esx_society:toggleSocietyHud', false)
end

function UpdateSocietyMoneyHUDElement(money)
	if ESX.GetConfig().EnableHud then
		ESX.UI.HUD.UpdateElement('society_money', {
			money = ESX.Math.GroupDigits(money)
		})
	end

	TriggerEvent('esx_society:setSocietyMoney', money)
end

function OpenBossMenu(society, close, options)
    options = options or {}
    local elements = {}

    ESX.TriggerServerCallback('esx_society:isBoss', function(isBoss)
        if isBoss then
            local defaultOptions = {
                withdraw = true,
                deposit = true,
                wash = true,
                employees = true,
                grades = true
            }

            for k, v in pairs(defaultOptions) do
                if options[k] == nil then
                    options[k] = v
                end
            end
			table.insert(elements, {
				title = _U('view_saldo_title'),
				description = _U('view_saldo_description'),
				icon = 'money-check-alt',
				onSelect = function()
					ESX.TriggerServerCallback('esx_society:getSocietyMoney', function(money)
						Config.Notification(_U('balance'), _U('balance_description', ESX.Math.GroupDigits(money)), 'inform')
					end, society)
				end
			})
			
			table.insert(elements, {
				title = _U('withdraw_money_title'),
				description = _U('withdraw_money_description'),
				icon = 'hand-holding-usd',
				onSelect = function()
					local input = lib.inputDialog(_U('withdraw_money_title'), {
						{ type = 'number', label = _U('salary_amount'), required = true }
					})
			
					if input and tonumber(input[1]) then
						local amount = tonumber(input[1])
						if amount > 0 then
							TriggerServerEvent('esx_society:withdrawMoney', society, amount)
						else
							Config.Notification(nil ,_U('invalid_amount'),'error')
						end
					end
				end
			})

            if options.deposit then
				table.insert(elements, {
					title = _U('deposit_money_title'), 
					description = _U('deposit_money_description'), 
					icon = 'piggy-bank',
					onSelect = function()
						local input = lib.inputDialog(_U('deposit_money_title'), {
							{ type = 'number', label = _U('salary_amount'), required = true } 
						})
			
						if input and tonumber(input[1]) then
							local amount = tonumber(input[1])
							if amount > 0 then
								TriggerServerEvent('esx_society:depositMoney', society, amount)
							else
                                
								Config.Notification(nil, _U('invalid_amount'),'error') 
							end
						end
					end
				})
			end
			
			if options.wash then
				table.insert(elements, {
					title = _U('wash_money_title'),
					description = _U('wash_money_description'),
					icon = 'water',
					onSelect = function()
						local input = lib.inputDialog(_U('wash_money_title'), {
							{ type = 'number', label = _U('salary_amount'), required = true } 
						})
			
						if input and tonumber(input[1]) then
							local amount = tonumber(input[1])
							if amount > 0 then
								TriggerServerEvent('esx_society:washMoney', society, amount)
							else
								Config.Notification(nil,_U('invalid_amount'), 'error' )
							end
						end
					end
				})
			end
			
			if options.employees then
				table.insert(elements, {
					title = _U('employee_management_title'),
					description = _U('employee_management_description'),
					icon = 'users',
					onSelect = function()
						OpenManageEmployeesMenu(society)
					end
				})
			end
			
			if options.grades then
				table.insert(elements, {
					title = _U('salary_management_title'), 
					description = _U('salary_management_description'),
					icon = 'dollar-sign',
					onSelect = function()
						OpenManageGradesMenu(society)
					end
				})
			end
			
			lib.registerContext({
				id = 'boss_menu_' .. society,
				title = _U('boss_menu_title'), 
				options = elements
			})
			

            lib.showContext('boss_menu_' .. society)
        end
    end, society)
end






function OpenManageEmployeesMenu(society)
    lib.registerContext({
        id = 'manage_employees_' .. society,
        title = _U('employee_management_title'),
        options = {
            {
                title = _U('employee_list_title'),
                description = _U('employee_list_description'),
                icon = 'users',
                onSelect = function()
                    OpenEmployeeList(society)
                end
            },
            {
                title = _U('recruit_title'),
                description = _U('recruit_description'),
                icon = 'user-plus',
                onSelect = function()
                    OpenRecruitMenu(society)
                end
            }
        }
    })
    
    lib.showContext('manage_employees_' .. society)
end

function OpenManageMenu(society, employee)
    ESX.TriggerServerCallback('esx_society:getJob', function(job)
        local options = {}

        for i=1, #job.grades, 1 do
            local gradeLabel = (job.grades[i].label == '' and job.label or job.grades[i].label)

            table.insert(options, {
                title = gradeLabel,
                description = _U('promote_employee_description'),
                icon = 'arrow-up',
                onSelect = function()
                    ESX.TriggerServerCallback('esx_society:setJob', function()
                        OpenEmployeeList(society)
                    end, employee.identifier, society, job.grades[i].grade, 'promote')
                end
            })
        end

        lib.registerContext({
            id = 'promote_employee_' .. society,
            title = _U('promote_employee_title', employee.name),
            options = options
        })

        lib.showContext('promote_employee_' .. society)
    end, society)
end

function OpenEmployeeList(society)
    ESX.TriggerServerCallback('esx_society:getEmployees', function(employees)
        local options = {}

        for i = 1, #employees, 1 do
            local gradeLabel = (employees[i].job.grade_label == '' and employees[i].job.label or employees[i].job.grade_label)

            table.insert(options, {
                title = employees[i].name .. ' - ' .. gradeLabel, 
                description = _U('employee'),
                icon = 'user-tie',
                onSelect = function()
                    OpenEmployeeOptionsMenu(society, employees[i])
                end
            })
        end
        lib.registerContext({
            id = 'employee_list_' .. society,
            title = _U('employee_list_title'),
            options = options  
        })

        lib.showContext('employee_list_' .. society)
    end, society)
end

function OpenEmployeeOptionsMenu(society, employee)
    local options = {}

    table.insert(options, {
        title = _U('promote_employee_title', employee.name),
        description = _U('promote_employee_description'),
        icon = 'arrow-up',
        onSelect = function()
            OpenManageMenu(society, employee)
        end
    })

    table.insert(options, {
        title = _U('fire_employee_title', employee.name),
        description = _U('fire_employee_description'),
        icon = 'user-slash',
        onSelect = function()
            FireEmployee(society, employee)
        end
    })

    lib.registerContext({
        id = 'employee_options_' .. employee.identifier,
        title = _U('employee_management_title', employee.name),
        options = options
    })

    lib.showContext('employee_options_' .. employee.identifier)
end

function FireEmployee(society, employee)
    ESX.TriggerServerCallback('esx_society:setJob', function()
        OpenEmployeeList(society)
    end, employee.identifier, 'unemployed', 0, 'fire')
end

function OpenRecruitMenu(society)
    ESX.TriggerServerCallback('esx_society:getOnlinePlayers', function(players)
        local options = {}

        for i=1, #players, 1 do
            if players[i].job.name ~= society then
                table.insert(options, {
                    title = players[i].name,
                    description = _U('recruit_description'),
                    icon = 'user-plus',
                    onSelect = function()
                        OpenRecruitConfirmMenu(society, players[i])
                    end
                })
            end
        end

        lib.registerContext({
            id = 'recruit_' .. society,
            title = _U('recruiting'),
            options = options
        })

        lib.showContext('recruit_' .. society)
    end)
end

function OpenRecruitConfirmMenu(society, player)
    lib.registerContext({
        id = 'recruit_confirm_' .. society,
        title = _U('recruit_confirm_title', player.name),
        options = {
            {
                title = _U('recruit_confirm_yes'),
                description = _U('hire_description'),
                icon = 'check',
                onSelect = function()

                    Config.Notification(nil, _U('you_have_hired', player.name), 'success')

                    ESX.TriggerServerCallback('esx_society:setJob', function()
                        OpenRecruitMenu(society)
                    end, player.identifier, society, 0, 'hire')
                end
            },
            {
                title = _U('recruit_confirm_no'),
                description = _U('cancel_recruitment'),
                icon = 'times',
                onSelect = function()
                    lib.hideContext()
                end
            }
        }
    })

    lib.showContext('recruit_confirm_' .. society)
end

function OpenManageGradesMenu(society)
    ESX.TriggerServerCallback('esx_society:getJob', function(job)
        local options = {}

        for i = 1, #job.grades, 1 do
            local gradeLabel = (job.grades[i].label == '' and job.label or job.grades[i].label)

            table.insert(options, {
                title = ('%s %s'):format(gradeLabel, _U('money_generic', ESX.Math.GroupDigits(job.grades[i].salary))),
                description = _U('salary_management_description'),
                icon = 'money-bill-wave', 
                onSelect = function()

                    local input = lib.inputDialog(_U('salary_management_title'), {
                        { type = 'number', label = _U('salary_amount'), required = true }
                    })

                    if input and tonumber(input[1]) then
                        local salary = tonumber(input[1])
                        if salary <= Config.MaxSalary then
                            ESX.TriggerServerCallback('esx_society:setJobSalary', function(success)
                                if success ~= nil then
                                    Config.Notification(nil, _U('salary_set_error'), 'error')
                                    OpenManageGradesMenu(society)
                                else
                                    Config.Notification(nil, _U('salary_updated'), 'success')
                                end
                            end, society, job.grades[i].grade, salary)
                        else
                            Config.Notification(nil, _U('invalid_amount_max'),'error' )
                        end
                    else
                        Config.Notification(nil, _U('invalid_amount'), 'error' )
                    end
                end
            })
        end

        lib.registerContext({
            id = 'manage_grades_' .. society,
            title = _U('salary_management_title'),
            options = options
        })

        lib.showContext('manage_grades_' .. society)
    end, society)
end



AddEventHandler('esx_society:openBossMenu', function(society, close, options)
    OpenBossMenu(society, close, options)
end)
